package de.hs.lucidityLog.database;import java.io.Serializable;import java.util.Objects;import androidx.annotation.Nullable;import androidx.room.Entity;@Entity(primaryKeys = {"id", "year", "month", "day"})public class Entry implements Serializable {    public int id;    public int year;    public int month;    public int day;    public String title;    public String text;    public boolean lucid;    public Entry(int id, int year, int month, int day, String title, String text, boolean lucid) {        this.id = id;        this.year = year;        this.month = month;        this.day = day;        this.title = title;        this.text = text;        this.lucid = lucid;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Entry entry = (Entry) o;        return id == entry.id &&                year == entry.year &&                month == entry.month &&                day == entry.day &&                lucid == entry.lucid &&                Objects.equals(title, entry.title) &&                Objects.equals(text, entry.text);    }    @Override    public int hashCode() {        return Objects.hash(id, year, month, day, title, text, lucid);    }    @Override    public String toString() {        return "Entry{" +                "id=" + id +                ", year=" + year +                ", month=" + month +                ", day=" + day +                ", title='" + title + '\'' +                ", text='" + text + '\'' +                ", lucid=" + lucid +                '}';    }}